import React, { createContext, useReducer, useEffect } from "react";
import useLocalStorage from "./useLocalStorage";

const AuthContext = createContext();

const initialState = {
  isAuthenticated: false,
};

const authReducer = (state, action) => {
  switch (action.type) {
    case "LOGIN":
      return {
        isAuthenticated: true,
      };
    case "LOGOUT":
      return {
        isAuthenticated: false,
      };
    default:
      return state;
  }
};

export const AuthProvider = ({ children }) => {
  const [storedAuthState, setStoredAuthState, removeStoredAuthState] = useLocalStorage("authState", initialState);
  const [state, dispatch] = useReducer(authReducer, storedAuthState);

  useEffect(() => {
    setStoredAuthState(state);
  }, [state, setStoredAuthState]);

  const logout = () => {
    dispatch({ type: "LOGOUT" });
    removeStoredAuthState();
  };

  const contextValue = {
    state,
    dispatch,
    logout,
  };

  return <AuthContext.Provider value={contextValue}>{children}</AuthContext.Provider>;
};

export const useAuth = () => {
  return useContext(AuthContext);
};
