export const checkAuth = () => {
  const persistedState = localStorage.getItem("authState");
  const isAuthValue = JSON.parse(persistedState);
  return isAuthValue && isAuthValue.isAuthenticated;
};

export const checkPrivateRoute = () => {
  const persistedState = localStorage.getItem("authState");
  const isAuthValue = JSON.parse(persistedState);
  return isAuthValue && isAuthValue.isAuthenticated;
};
