import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import "../styles/publicPage.scss";
import BoyVector from "../images/boyVector.svg";
import BackgroundLayers from "../images/bgMain.png";
import { checkAuth } from "../utils/authUtils"; // Import the utility function


const PublicPage = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  const navigate = useNavigate();
  const { dispatch } = useAuth();

  useEffect(() => {
    if (checkAuth()) {
      navigate("/private");
    } else {
      navigate("/");
    }
  }, []);

  const handleLogin = () => {
    if (email === "user@test.com" && password === "1234") {
      dispatch({ type: "LOGIN" });
      setSuccess("Login successful!");
      setError("");
      navigate("/private");
    } else {
      setError("Invalid email or password. Please try again.");
      setSuccess("");
    }
  };
  const handleInputChange = () => {
    setError("");
    setSuccess("");
  };
  return (
    <div className="main_section">
      <div className="main_wrapper">
        <div className="login_form">
          <div className="login_wrapper">
            <h1>Login</h1>
            <div className="input_group">
              <input
                type="email"
                placeholder="Enter Email"
                value={email}
                onChange={(e) => {
                  setEmail(e.target.value);
                  handleInputChange();
                }}
              />
            </div>
            <div className="input_group">
              <input
                type="password"
                placeholder="Enter Password"
                value={password}
                onChange={(e) => {
                  setPassword(e.target.value);
                  handleInputChange();
                }}
              />
            </div>
            <p>
              {error && <p className="error_message validation_msg">{error}</p>}
              {success && <p className="success_message validation_msg">{success}</p>}
            </p>
            <button onClick={handleLogin}>Login</button>
          </div>
        </div>
        <div className="vector_section">
          <img src={BackgroundLayers} />
          <img className="main_vector" src={BoyVector} />
        </div>
      </div>
    </div>
  );
};

export default PublicPage;
